import { Component } from '@angular/core';
import { ActividadesService } from 'src/app/services/actividades.service';
import { Actividades } from 'src/app/models/actividades';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-detalle-evento-voluntario',
  templateUrl: './detalle-evento-voluntario.component.html',
  styleUrls: ['./detalle-evento-voluntario.component.css']
})
export class DetalleEventoVoluntarioComponent {
  public fecha_inicio: any = "";
  public hora_inicio: any = "";
  public maximo_voluntarios: any = 0;
  public contador_voluntarios: any = 0;
  public lugar_evento: any = "";
  public descripcion_evento: any = "";
  public titulo_evento: any = "";
  public num_embarcaciones: number = 0;
  public num_motos: number = 0;
  public num_tripulantes: number = 0;
  public num_patrones: number = 0;
  public num_socorristas: number = 0;
  public user_joined: boolean = false;
  public isDisabled: boolean = false;
  public entidad_organizadora: string = "";
  public unido: boolean = false;
  public idEvento: any = 0;
  public id: any = sessionStorage.getItem('id');
  public resp: Actividades[] = [];

  public constructor(public service: ActividadesService, private route: ActivatedRoute) {  }

  ngOnInit() {
    this.idEvento = this.route.snapshot.paramMap.get('id');
