import { Component } from '@angular/core';
//IMPORTAMOS EL SERVICIO
import { ActividadesService } from 'src/app/services/actividades.service';

//IMPORTAMOS LAS INTERFACES
import { Actividades } from 'src/app/models/actividades';

@Component({
  selector: 'app-lista-evento-voluntario',
  templateUrl: './lista-evento-voluntario.component.html',
  styleUrls: ['./lista-evento-voluntario.component.css']
})
export class ListaEventoVoluntarioComponent {

  public maximo_voluntarios: number = 30;
  public contador_voluntarios: number = 15;
  public resp: Actividades[] = [];
  public isDisabled: boolean = false;
  public unido: boolean = false;
  public id: any = sessionStorage.getItem('id');
  public horaFormateada: string = "";
  public fechaFormateada: string = "";

  public constructor(public service: ActividadesService) { }

  ngOnInit() {
    this.getListadoEventos();
    if (this.contador_voluntarios >= this.maximo_voluntarios) {
      this.isDisabled = true;
    }

  }

  public detalle(idEvento: number) {
    console.log(idEvento);
    this.service.getDetalle(idEvento, this.id).subscribe(response => {
      console.log(response);
      let fecha = new Date(this.resp[0].FECHA_INICIO)
      localStorage.setItem('nombre_evento', this.resp[0].NOMBRE);
      localStorage.setItem('lugar_evento', this.resp[0].UBICACION);
      localStorage.setItem('descripcion_evento', this.resp[0].OBSERVACIONES);
      localStorage.setItem('fecha_inicio_evento', fecha.to);
      localStorage.setItem('hora_inicio_evento', this.resp[0].HORA_INICIO);
      localStorage.setItem('voluntarios_evento', this.resp[0].VOLUNTARIOS.toString());
      localStorage.setItem('max_voluntarios_evento', this.resp[0].MAX_VOLUNTARIOS.toString());
    });
  }

  public getListadoEventos() {

    this.service.getActividades(this.id).subscribe((response: any[]) => {
      for (let i = 0; i < response.length; i++) {
        if (response[i].ES_FORMACION != true) {
          this.resp = response;
          let hora_inicio = new Date(this.resp[0].HORA_INICIO);
          let horas = hora_inicio.getHours();
          let minutos = hora_inicio.getMinutes();
          let segundos = hora_inicio.getSeconds();
          this.horaFormateada = `${horas.toString().padStart(2, '0')}:${minutos.toString().padStart(2, '0')}:${segundos.toString().padStart(2, '0')}`;
          let fecha_inicio = new Date(this.resp[0].FECHA_INICIO);
          let dia = fecha_inicio.getDate();
          let mes = fecha_inicio.getMonth() + 1;
          let any = fecha_inicio.getFullYear();
          this.fechaFormateada = `${dia.toString().padStart(2, '0')}/${mes.toString().padStart(2, '0')}/${any}`;
        }
      }

    })
  }


  public unirse(voluntarios: number, voluntariosMax: number) {

    voluntarios++;

    this.unido = true;
    if (voluntarios >= voluntariosMax) {
      this.isDisabled = true;
    }

  }

  public desunirse(id: number) {

    this.contador_voluntarios--;
    this.unido = false;
    if (this.contador_voluntarios >= this.maximo_voluntarios) {
      this.isDisabled = true;
    }

  }

}
