import { Injectable } from '@angular/core';
//IMPORTAMOS LOS SERVICIOS
import { Observable } from 'rxjs';
import { HttpClient } from '@angular/common/http';

//IMPORTAMOS LAS INTERFACES
import { Actividades } from '../models/actividades';

@Injectable({
  providedIn: 'root'
})
export class ActividadesService {

  constructor(public http: HttpClient) { }

  public listado_actividades = "https://localhost:8000/api/listActividades/";
  public detalleActividad = "https://localhost:8000/api/listActividades/";

  public getActividades(id: string):Observable<any>{
    return this.http.get<Actividades>(this.listado_actividades + `${id}`);
  }


  public getDetalle(id: number){
    return this.http.get<Actividades>(this.listado_actividades + `${id}`);
  }

}
